INSERT INTO books (name, author, publisher, yearPublished, category, image, evaluation, booksPurchased, price, description) VALUES ('Shadow Slave', 'Guiltythree', 'WebNovel', 2022, 'Ação', 'https://i1.sndcdn.com/artworks-KlzGzGAyG7Bo8CYl-o9LyAw-t500x500.jpg', 4, 53100000, 84.99, 'Sunny, um jovem pobre, é escolhido pelo Feitiço do Pesadelo e ganha poderes sobrenaturais. Transportado para um mundo em ruínas, enfrenta monstros e outros Despertos, mas seu poder tem um efeito colateral mortal.');
INSERT INTO books (name, author, publisher, yearPublished, category, image, evaluation, booksPurchased, price, description) VALUES ('Childhood Friend of The Zenith', 'Woobi Ram', 'NovelPia', 2021, 'Ação', 'https://slimeread.com/_next/image?url=https%3A%2F%2Fblack.slimeread.com%2Fcovers%2Famigo-de-infancia-da-zenite.jpeg&w=384&q=100', 4, 42153977, 50.00, 'Gu Yangchun, um humano demoníaco arrependido, regride no tempo até conhecer Wi Seol-ah. Decidido a viver em paz, ele vê seu passado sombrio voltar para assombrá-lo.');
INSERT INTO books (name, author, publisher, yearPublished, category, image, evaluation, booksPurchased, price, description) VALUES ('O Hobbit', 'J. R. R. Tolkien', 'George Allen & Unwin', 1937, 'Ação', 'https://upload.wikimedia.org/wikipedia/pt/thumb/7/72/The_Hobbit_Cover.JPG/230px-The_Hobbit_Cover.JPG', 5, 100000000, 35.92, 'Bilbo Bolseiro, um hobbit pacato, é arrastado por Gandalf e anões em uma jornada para roubar o tesouro de Smaug, o dragão. Uma aventura que mudará sua vida para sempre.');
INSERT INTO books (name, author, publisher, yearPublished, category, image, evaluation, booksPurchased, price, description) VALUES ('A Grande Solidão', 'Kristin Hannah', 'Arqueiro', 2018, 'Drama', 'https://m.media-amazon.com/images/I/61SahnaFnEL._AC_UF1000,1000_QL80_.jpg', 5, 2562, 40.00, 'Um veterano do Vietnã leva a família para o Alasca em busca de recomeço, mas o inverno rigoroso revela traumas perigosos que ameaçam sua família.');
