INSERT INTO books (name, author, publisher, yearPublished, category, image, evaluation, booksPurchased, price, description) VALUES ('O Jardim das Borboletas', 'Dot Hutchison', 'Planeta', 2017, 'Suspense', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQaI8v_gMY4qyp1Jrp00a0wtCr5qzMIw0vWXh7UTsjuCxtPjlMk', 5, 4542, 68.08, 'Jovens mulheres são sequestradas por um psicopata conhecido como Jardineiro e mantidas em um jardim sinistro. Uma sobrevivente relata os horrores ao FBI.');
